package ProjetoAula01;

public class Valores {
	private int valorDigitado;
	private int valorArmazenado;
	
	public Valores(int valorDigitado, int valorArmazenado) {
		// TODO Auto-generated constructor stub
		this.valorDigitado=valorDigitado;
		this.valorArmazenado=valorArmazenado;
	}
	
	public int getValorDigitado() {
		return valorDigitado;
	}
	public void setValorDigitado(int valorDigitado) {
		this.valorDigitado = valorDigitado;
	}
	public int getValorArmazenado() {
		return valorArmazenado;
	}
	public void setValorArmazenado(int valorArmazenado) {
		this.valorArmazenado = valorArmazenado;
	}
	
	

}
